import React, { useState } from "react";
import "./OnTypeEx.css";

const OnTypeEx = (props) => {
   const [textField, setTextField] = useState("This text should be changed on typing");
   const OnType = (event) => {
      setTextField(event.target.value);
   };
   const Onsubmit = (event) => {
      setTextField(event);
   };
   return (
      <div>
         <form onSubmit={Onsubmit}>
            <input type="text" onChange={OnType} placeholder="Type here"></input>
            <p className="text-color">{textField}</p>
         </form>
      </div>
   );
};

export default OnTypeEx;
