import React, {Component} from 'react';

class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            yourName : ''
        }
    }

    updateValue(event) {
        this.setState({
            yourName : event.target.value
        })
    }

    render() {
        return (
            <div>
                <label>Your name:</label>
                <input type="text"
                       onChange={(event) => this.updateValue(event)}/>
            </div>
        );
    }
}

export default Register;
